import React from "react";
import { Container, styled, Typography } from "@mui/material";
import { ThemeProvider } from "@mui/material/styles";
import banner from "../../../images/banner.svg";
import theme from "../../themes/theme";

const MainBannerContainer = styled("div")({
  width: 890,
  height: 264,
  backgroundColor: "#F1F6F4",
  display:'flex',
    gap: "112px",
    margin: "0 auto",
});

const LeftBannerContainer = styled("div")({
  display: "flex",
  flexDirection: "column",
  position: "relative",
  top: 45,
  left: 45,
  bottom: 45,
  gap: 15,
});
const LeftTopContainer = styled("div")({
  width: "319px",
  height: "90px",
});
const LeftBottomContainer = styled("div")({
  width: "461px",
  height: "69px",   
});



export const Banner = () => {
  return (
    <ThemeProvider theme={theme}>
      <Container>
        <MainBannerContainer>
          <LeftBannerContainer>
            <HeaderContainer>
              <Typography variant="h1">
                Explore Books on Entrepreneurship
              </Typography>
            </HeaderContainer>
            <DescriptionContainer>
              <Typography variant="subtitle2">
                Everything you need to know about thriving on a<br></br>
                shoestring budget, making your first million, and hiring{" "}
                <br></br> right from the start.
              </Typography>
            </DescriptionContainer>
          </LeftBannerContainer>
          <ImageContainer>
            <img src={banner} />
          </ImageContainer>
        </MainBannerContainer>
      </Container>
    </ThemeProvider>
  );
};
