import { Box, Typography } from "@mui/material";
import { styled, makeStyles } from "@mui/styles";
import { ThemeProvider } from "@mui/material/styles";
import theme from "../../themes/theme";
import { useEffect, useState } from "react";
import { IconAndText } from "/home/vishaly/Blinkist/my-blinlist/src/components/molecules/IconAndText/index";
import { ReactComponent as User } from "../../../images/UserIcon.svg";
import { ReactComponent as Time } from "../../../images/time.svg";
import { ReactComponent as Add } from "../../../images/add.svg";
import {ButtonComponent} from "/home/vishaly/Blinkist/my-blinlist/src/components/atoms/Button/index.tsx";
import ProgressBar from "../../atoms/ProgressBar/index";
import {useNavigate} from 'react-router-dom'
import api from './../../../api/api'

let MainContainer = styled("div")({
  width: "284px",
  height: "485px",
  borderRadius: 8,
  border: "1px solid #E1ECFC",
});

let ImageContainer = styled("div")({
  width: 284,
  height: 292,
});

let InfoContainer = styled("div")({
  position: "relative",
  top: 23,
  left: 16,
  width: 224,
  height: 95,
});

let OtherDetails = styled("div") ({
  display: 'flex',
  flexDirection: 'row',
  position: 'relative',
  top: 17,
  left: -10
})

let TimeToReadDiv = styled("div") ({
  left: 17.67,
  fontSize: 14,
  position: 'relative'
})

let NumberOfReads = styled("div") ({
  position: 'relative',
  left: 47.33,
  fontSize: 14
})

let ButtonDiv = styled("div") ({
  position: 'relative',
  top: 10
})

let useStyles = makeStyles({
  author: {
    width:'fit-content',
    position: "relative",
    top: 16,
    color: "#6D787E",
    fontSize: 16,
    fontWeight: 500,
  },
  title: {
    width:"250px",

    fontWeight: 600,
    fontSize: '18px !important',
    color: "#03314B",
  },
  image: {
    width: 16.67,
    height: 16.67,
  },
  finished: {
    color: '#0365F2 !important',
    position: 'relative',
    left: 111,

    //top: 30,
    bottom:'5px',
    fontWeight: 500,
    fontSize: 16
  },
  button: {
    width: '100%',
    height: 52,
    color: '#0365F2 !important',
    "&:hover": {
      color: 'white !important',
      backgroundColor: '#0365F2 !important',
      fill: 'white'
    }
  }
});

export interface CardProps {
  title?: string;
  image?: string;
  author?: string;
  isFinished?: boolean;
  addToLibrary?: boolean;
  currentlyReading?: boolean;
  readAgain?: boolean;
  id?: number;
  timeToRead?: string;
  numberOfReads?: string;
  value: number;
  className?: string;
  onClick?: () => void;
}

export type BookInfo = {
  id: number,
  title: string,
  author: string,
  image: string,
  timeToRead: string,
  numberOfReads: string,
  status: {
    isFinished: boolean,
    isTrending: boolean,
    justAdded: boolean,
    isFeatured: boolean
  }
}

export const Card = (props: CardProps) => {
  let {value, onClick, image, title, author, timeToRead, numberOfReads, readAgain, addToLibrary, isFinished } = props
  let styles = useStyles();

  let [iconStyle, setIconStyle] = useState({})

  let handleMouseEnterEvent = () => {
    setIconStyle({
      fill: 'white',
      stroke: 'white'
    })
  }

  let handleMouseLeaveEvent = () => {
    setIconStyle({})
  }

  const [bookInfo, setBookInfo] = useState<BookInfo>({
    id: 1,
    title: "",
    author: "",
    image: "",
    timeToRead: "",
    numberOfReads: "",
    status: {
      isFinished: false,
      isTrending: false,
      justAdded: false,
      isFeatured: false
    }
  });

  useEffect(() => {
    const retrieveBookById = async(val: number) => {
      if(val && val != 0) {
        const book = await api.get(`/library/${val}`)
        const data = book.data
        setBookInfo(data)
      }
    }

    retrieveBookById(value)
  }, [value])

  let addToCurrentlyReading = async (num: number) => {
    console.log("book: "+JSON.stringify(bookInfo))
    bookInfo.status.isFinished = false
    bookInfo.status.isTrending = false

    await api.put(`/library/${num}`, bookInfo)
  }

  let updateFinish = async (num: number) => {
    if(bookInfo.status.isFinished) {
      bookInfo.status.isFinished = false
    }
    else {
      bookInfo.status.isFinished = true
    }

    await api.put(`/library/${num}`, bookInfo)
  }

 let navigate = useNavigate()

  let showDetailsPage = () => {
   navigate('/bookdetails')
  }

  return (
    <ThemeProvider theme={theme}>
      <MainContainer onClick={onClick}>
        <ImageContainer>
          <img src={image} />
        </ImageContainer>
        <InfoContainer>
          <Typography
            variant="subtitle1"
            sx={{ color: "#03314B" }}

            className={styles.title}
            onClick={showDetailsPage}
          >
            {title}
          </Typography>
          <Typography variant="body1" className={styles.author}>
            {author}
          </Typography>
        </InfoContainer>
        <OtherDetails>
          <TimeToReadDiv>
          {!timeToRead ? null : (
              <IconAndText iconSource={<Time />} title={timeToRead} variant="caption" />
            )}
          </TimeToReadDiv>
          <NumberOfReads>
          {!numberOfReads ? null : (
              <IconAndText iconSource={<User />} title={numberOfReads} variant="caption" />
            )}
          </NumberOfReads>
        </OtherDetails>

        {isFinished ? (
          <div style={{height: 52, position:'relative',top:30, bottom:0}}>
            <Typography variant="body1" className={styles.finished} onClick={() => updateFinish(value)} style={{cursor:'pointer'}} >
              Finished
            </Typography>
            <Box
            sx={{
                width: 284,
                bgcolor: '#F1F6F4',
                mt:0,
                borderRadius: 8,
                boxSizing: 'border-box',
              }}
            >
              <ProgressBar progress={30} />
            </Box>
            </div>

        ):null}



        {readAgain ? ( 
          <div style={{height: 52, position:'relative',top:30, bottom:0}}>
            <Typography variant="body1" className={styles.finished} onClick={() => updateFinish(value)} style={{cursor:'pointer'}}>
              Read Again
            </Typography>
            <Box
            sx={{
                width: 284,
                bgcolor: '#F1F6F4',
                mt:0,
                borderRadius: 8,
                boxSizing: 'border-box',
              }}
            >
              <ProgressBar progress={100} />
            </Box>
            </div>
        ) : null}

        {addToLibrary ? (
          <ButtonDiv>
            <ButtonComponent startIcon={<Add style={iconStyle} />} className={styles.button} onClick={() => addToCurrentlyReading(value)} onMouseEnter={handleMouseEnterEvent} onMouseLeave={handleMouseLeaveEvent}>Add To Library</ButtonComponent>
          </ButtonDiv>
        ) : null}



      </MainContainer>




    </ThemeProvider>
  );
};
